const VERSION = 'photo-0-0-3';

var urlsToCache = [
  "https://www.goodwell.top/static/js/app.1.0.3.css",
  "https://www.goodwell.top/static/js/manifest.1.0.3.js",
  "https://www.goodwell.top/static/js/vendor.1.0.3.js",
  "https://www.goodwell.top/static/js/app.1.0.3.js"
];


self.addEventListener('install', function(event) {
  caches.open(VERSION)
    .then(function(cache) {
      return cache.addAll(urlsToCache);
    });
});
// 缓存更新
self.addEventListener('activate', function(event) {
  event.waitUntil(
    caches.keys().then(function(cacheNames) {
      return Promise.all(
        cacheNames.map(function(cacheName) {
          // 如果当前版本和缓存版本不一致
          if (cacheName !== VERSION) {
            return caches.delete(cacheName);
          }
        })
      );
    })
  );
});

self.addEventListener('fetch', function(event) {
  const {
    request
  } = event;
  if (!request.url.match(/.js/) || !request.url.match(/.css/) || request.url.match(/.mp4/)) return;
  const findResponsePromise = caches.open(VERSION)
    .then(function(cache) {
      cache.match(request)
    })
    .then(function(response) {
      if (response) {
        return response;
      }

      return fetch(request);
    });

  event.respondWith(findResponsePromise);
});